pragma solidity ^0.4.18;

contract Splitter{
    address[] addressKeeper;   //Guarda las direcciones de Alice,Bob y Carol
    uint  bobPending;           //Si Alice envía eth, esta variable guarda el monto para bob
    uint  carolPending;         // guarda el monto para carol
    address public owner;             //el dueño del contrato

    event Logcreator(address owner, address Bob, address Carol);
    event LogSending(uint bob,uint Carol); 

    modifier onlyOwner(){
        require(owner==msg.sender);   //Solo el dueño puede modificar
        _;
    }
//cuando se crea el contrato, declara quien es el dueño   
    function Splitter() public payable{
        owner=msg.sender;
    }
//función fallback
       function() public payable{
	}

//guardar las direcciones de Alice, Bob y Carol (Solo el dueño la puede modificar).
    function setReceivers(address _Bob, address _Carol) onlyOwner public{
	require(msg.sender!=_Bob && msg.sender!=_Carol && _Bob!=_Carol);
        addressKeeper.push(msg.sender);
        addressKeeper.push(_Bob);
        addressKeeper.push(_Carol);
        Logcreator(msg.sender,_Bob,_Carol);
	}  
//Alice decide enviar los ethers a Bob y Carol    
    function send() payable onlyOwner public{
        require(addressKeeper[1]!=0x000);
        require(addressKeeper[2]!=0x000);
        require(address(this).balance>0);
            bobPending=address(this).balance/2;     //una mitad para bob
            carolPending=address(this).balance/2;//la otra mitad a Carol
            LogSending(bobPending,carolPending);
    }

//retirar el dinero pendiente.
    function withDraw() payable public{
        require(bobPending+carolPending>0); //solo si está pendiente la transacción
        require(msg.sender==addressKeeper[1] || msg.sender==addressKeeper[2]);
        if(msg.sender == addressKeeper[1]){
            addressKeeper[1].transfer(bobPending);  //Solo Bob puede retirar el dinero
            bobPending=0;
            }
        if(msg.sender == addressKeeper[2]){
            addressKeeper[2].transfer(carolPending); //Solo Carol puede retirar el dinero
            carolPending=0;
            }
    }

    function contractBalance() public view returns(uint){
        return( address(this).balance);
    }

    function participantBalance() public view returns( uint, uint, uint){
        return(addressKeeper[0].balance,addressKeeper[1].balance,addressKeeper[2].balance);
    }

    function emergency()public onlyOwner payable{
        require(address(this).balance>0);   //si el balance es mayor a 0
        owner.transfer(address(this).balance); //devuelva el dinero del contrato al dueño
        selfdestruct(owner);  //que se destruya el contrato
    }

}
